if (process.platform === 'win32') {
  const o = require(`./win32-${process.arch}.node`)
  if (parseFloat(require('os').release()) >= 6.2) {
    o.uwp = require(`./win32-uwp-${process.arch}.node`)
  }
  module.exports = o
} else {
  module.exports = require(`./${process.platform}-${process.arch}.node`)
}
